mapper.insert:
INSERT INTO STUDYROOM_BOARD (
    BOARD_TITLE
    , BOARD_WRITER
    , BOARD_CONTENT
    , BOARD_SECRET
    , BOARD_CODE
) VALUES (
    #{boardTitle}
    , #{boardWriter}
    , #{boardContent}
    , IFNULL(#{boardSecret}, 'NO')
    , #{boardCode}
)

mapper.select:
SELECT BOARD_CODE , BOARD_TITLE , DATE_FORMAT(BOARD_DATE, '%Y-%m-%d') BOARD_DATE , BOARD_WRITER , BOARD_SECRET , BOARD_CONTENT , BOARD_ANSWER FROM STUDYROOM_BOARD
WHERE ${searchType} LIKE CONCAT ('%' , #{searchValue} , '%') AND BOARD_SECRET = 'NO'
ORDER BY BOARD_CODE DESC LIMIT #{displayDateCnt} OFFSET ${displayDateCnt * (nowPage-1)}

mapper.update:
UPDATE STUDYROOM_MEMBER
SET
MEMBER_NAME = #{memberName}
, MEMBER_PW = #{memberPw}
, MEMBER_TEL = #{memberTel}
, MEMBER_BIRTH = #{memberBirth}
, MEMBER_ADDR = #{memberAddr}
, ADDR_DETAIL = #{addrDetail}
WHERE MEMBER_CODE = #{memberCode}

mapper.insert:
INSERT INTO STUDYROOM_COMMENT (
    COMMENT_WRITER
    , COMMENT_CONTENT
    , BOARD_CODE
) VALUES (
    #{commentWriter}
    , #{commentContent}
    , #{boardCode}
)
